import React, { useState } from "react";
import { ToastContainer, toast ,  } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import '../index.css'; 
import Modal from '../Modal.jsx'
let toastCounter = 0;

const ToastWithIndex = ({  index }) => (
    <div className="custom-toast">

        <div>{`Testing: ${index}`}</div>
        
    </div>
);

function Comp1() {

           

    
    const [customCounter, setCustomCounter] = useState(0); // State for custom counter
    const [showModal, setShowModal] = useState(false); // State for modal visibility

    const [customTime, setCustomTime] = useState(5); // State for custom time in seconds, default 5 seconds

    const showToastMessage = () => {
        const index = ++toastCounter;
        toast(<ToastWithIndex  index={index} />, {
            position: "bottom-center",
            autoClose: customTime * 1000, // Convert seconds to milliseconds
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            closeButton: true,
            className: 'custom-toast',
            bodyClassName: 'custom-toast', 
            progressClassName: 'custom-toast' 
        });
    };

    const toggleModal = () => {
        setShowModal(!showModal);
    };

   const handleSetTime = (timeInSeconds) => {
        setCustomTime(timeInSeconds);
        toggleModal(); // Close modal after setting time
    };
    
    
    return (
        <>
            <div className="container m-5">
                <div className="row p-0">
                    <div className="col-md-5">
                        <button className="bg-primary p-1 fs-4 rounded border border-transparent" onClick={toggleModal}>
                            Show Toast Message
                        </button>
                        <Modal isOpen={showModal} onClose={toggleModal} setTime={handleSetTime} />
                    </div>
                </div>
            </div>
            <ToastContainer className="toast-container" />
        </>
    );
}

export default Comp1;
