import React, { useEffect } from 'react';
import './toast.css';

const Toast = ({ message, duration, onClose }) => {
    useEffect(() => {
        const timer = setTimeout(() => {
            onClose();
        }, duration);

        return () => clearTimeout(timer);
    }, [duration, onClose]);

    return (
        <div className="toast show">
            <span>{message}</span>
            <button onClick={onClose}>&times;</button>
        </div>
    );
};

export default Toast;
