import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import '../index.css'; // Ensure this import matches your CSS file location
import ToastWithIndex from "./ToastWithIndex";

let toastCounter = 0;

function Comp1() {
    const [customCounter, setCustomCounter] = useState(0); // State for custom counter

    // Function to show toast message
    const showToastMessage = () => {
        const index = ++toastCounter;
        toast(<ToastWithIndex index={index} />, {
            position: "bottom-center",
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            closeButton: true,
            className: 'custom-toast',
            bodyClassName: 'custom-toast', 
            progressClassName: 'custom-toast' 
        });
    };

    // Function to set custom counter
    const setCounter = () => {
        setCustomCounter(customCounter + 1); // Increment counter
    };

    return (
        <>
            <div className="container m-5">
                <div className="row p-0">
                    <div className="col-md-5">
                        <button className="bg-primary p-1 fs-4 rounded border border-transparent" onClick={showToastMessage}>
                            Show Toast Message
                        </button>
                        <button className="px-2 py-2 rounded border-0 ms-3" onClick={setCounter}>
                            <i className="fa fa-gear fs-5"></i>
                        </button>
                    </div>
                </div>
            </div>
            <ToastContainer className="toast-container" />
        </>
    );
}

export default Comp1;
