import React from "react";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import '../index.css'; // Ensure this import matches your CSS file location

function Comp1() {

            let toastCounter = 0;

        const ToastWithIndex = ({ message, index }) => (
            <div className="custom-toast">
                <div className="toast-index">{index}</div>
                <div>{message}</div>
            </div>
        );
        
        const index = ++toastCounter;


    const showToastMessage = () => {
        toast(<ToastWithIndex message="This is a custom toast message!" index={index} />, {
            position: "bottom-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            closeButton: true,
            className: 'custom-toast',
            bodyClassName: 'custom-toast', 
            progressClassName: 'custom-toast' 
        });
    };

    
    return (
        <>
            <div className="container m-5">
                <div className="row p-0">
                    <div className="col-md-5">
                        <button className="bg-primary p-1 fs-4 rounded border border-transparent" onClick={showToastMessage}>
                            Show Toast Message
                        </button>
                        <button className="px-2 py-2 rounded border-0 ms-3">
                            <i className="fa fa-gear fs-5"></i>
                        </button>
                    </div>
                </div>
            </div>
            <ToastContainer className="toast-container" />
        </>
    );
}

export default Comp1;
