import React from "react";
import { ToastContainer , toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';


function Comp1(){
    const hello = ()=>{
        toast("This is a toast message!", {
            position: "bottom-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            closeButton: true
        });
    };
    return(
        <>
           
            <div className="container m-5 ">
                <div className="row p-0">
                    <div className="col-md-5">
            <button className="bg-primary p-1 fs-4 rounded border border-transparent" onClick={hello} >Show Toast Message</button> 
            <button className="px-2 py-2 rounded border-0 ms-3 ">
                                <i className="fa fa-gear fs-5" ></i>
                            </button> 
                    </div>
                   
                </div>
            </div>
                 
        </>
    )
}
export default Comp1;
