import React, { useState } from "react";
import { ToastContainer, toast ,  } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import '../index.css'; 
// import Modal from '../Modal.js'

let toastCounter = 0;

const ToastWithIndex = ({  index }) => (
    <div className="custom-toast">

        <div>{`Testing: ${index}`}</div>
        
    </div>
);

function Comp1() {
    const [modalOpen, setModalOpen] = useState(false);

    const showToastMessage = () => {
        const index = ++toastCounter;
        toast(<ToastWithIndex  index={index} />, {
            position: "bottom-center",
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            closeButton: true,
            className: 'custom-toast',
            bodyClassName: 'custom-toast', 
            progressClassName: 'custom-toast' 
        });
    };

    const openModal = () => {
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
    };
   
    
    return (
        <>
            <div className="container m-5">
                <div className="row p-0">
                    <div className="col-md-5">
                        <button className="bg-primary p-1 fs-4 rounded border border-transparent" onClick={showToastMessage}>
                            Show Toast Message
                        </button>
                        <button className="px-2 py-2 rounded border-0 ms-3 "onClick={openModal} >
                            <i className="fa fa-gear fs-5"></i>
                        </button>
                    </div>
                </div>
            </div>
            <ToastContainer className="toast-container" />


            <Modal isOpen={modalOpen} closeModal={closeModal}>
                
                <h2>Modal Title</h2>
                <p>This is the modal content.</p>
                <button onClick={closeModal}>Close Modal</button>
            </Modal>
        </>
    );
}

export default Comp1;
