import React, { useState } from "react";
import { ToastContainer, toast ,  } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import '../index.css'; 
// import Modall from '../Modall.jsx'

let toastCounter = 0;

const ToastWithIndex = ({  index }) => (
    <div className="custom-toast">

        <div>{`Testing: ${index}`}</div>
        
    </div>
);

function Comp1() {

    const [timeout, setTimeout] = useState(7000); 

    const showToastMessage = () => {
        const index = ++toastCounter;
        toast(<ToastWithIndex  index={index} />, {
            position: "bottom-center",
            autoClose: 5000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,    
            closeButton: true,
            className: 'custom-toast',
            bodyClassName: 'custom-toast', 
            progressClassName: 'custom-toast' 
        });
    };

    const handlechange = (event) => {
        setTimeout(event.target.value);
    }

    const handlesubmit = () => {
        setTimeout(timeout*1000);

    }
  
   
    
    return (
        <>
            <div className="container m-5">
                <div className="row p-0">
                    <div className="col-md-5">
                        <button className="bg-primary p-1 fs-4 rounded border border-transparent" onClick={showToastMessage}>
                            Show Toast Message
                        </button>
                        <button className="px-2 py-2 rounded border-0 ms-3 " ata-bs-target="#exampleModalToggle" data-bs-toggle="modal" >
                            <i className="fa fa-gear fs-5"></i>
                        </button>
                    </div>
                </div>
            </div>
            <ToastContainer className="toast-container" />

            
            <div className="modal fade" id="exampleModalToggle" aria-hidden="true" aria-labelledby="exampleModalToggleLabel" tabIndex="-1">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content">
                        <div className="modal-body">
                            <div className='d-flex justify-content-between'>
                                <h1 className="modal-title fs-5" id="exampleModalToggleLabel"></h1>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>

                            <div className='d-flex mt-3'>
                                <div>
                                    <label htmlFor="">Set Timeout : </label>
                                </div>
                                <div>
                                    <input type="number" className='form-control ms-5' onChange={handlechange}/>
                                </div>
                            </div>

                            <br />
                            <div className='text-center mt-3'>
                                <button className="btn btn-success" data-bs-dismiss="modal" aria-label="Close" onClick={handlesubmit}>Confirm</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        

        </>
    );
}

export default Comp1;
